import React from 'react'

const TableHead = (props) => {
    return (
        <div style={props.styles} className = "table-head">

            <div>SerNo</div>
            <div>Name</div>
            <div>RollNo</div>
            <div>Course</div>
            <div>Semester / Class</div>
            <div>Branch / Section</div>
            <div>Action</div>
        </div>
    )
}

const TableBody = (props) => {

    // the following style will be use while clicked on a button
    const presentStyles = {display:props.student.isPresent ==="p" ? "initial" : props.student.isPresent ==="a" ? "none":null};

    const absentStyles = {display:props.student.isPresent ==="a" ? "initial" : props.student.isPresent ==="p" ? "none":null}
    return (

        <div style={props.styles} className = "table-body">
            <div>{props.student.serNo}</div>
            <div>{props.student.stdName}</div>
            <div>{props.student.stdRollNo}</div>
            <div>{props.student.stdCourse}</div>
            <div>{props.student.stdClass}</div>
            <div>{props.student.stdBranch}</div>

            <div style={{position:"relative"}}>
            <button
             className='btn-present'
             style={presentStyles}
             onClick={()=>props.present(props.student.serNo)}
            >P</button>

            <button
             className='btn-absent' 
             style={absentStyles}
             onClick = {()=>props.absent(props.student.serNo)}
            >A</button>
            <span className='delete' onClick={()=>props.delete(props.student.serNo)}>X</span>
            </div>
        </div>
    )
}
const StudentsInClass = (props) => {
    const styles = { display: "grid", gridTemplateColumns: "repeat(7 ,12vw)", padding: "10px" }

    let students ;
     localStorage.getItem(props.data.teaRegEmail) === null ? students = []   
     :students = JSON.parse(localStorage.getItem(props.data.teaRegEmail))
    const studentsComponents = students.map(student =>{
        return <TableBody student = {student} present ={props.present} absent = {props.absent} delete = {props.delete} styles = {styles} key = {student.serNo}/>
    })
    
    return (

        <div>
            <div className='table-caption'>Total stringth in the class <span>{students.length}</span></div>

            <div style={{border:"2px solid gainsboro" , margin:"2px 0 0 3px"}}>
            <TableHead styles={styles} data = {props.data}/>
            {studentsComponents}
            </div>
       

        </div>
    )
}

export default StudentsInClass;

export {TableBody , TableHead}
