import React from 'react'

const Title = (props) => {
    const dateStyles = { color: "#fb4747", borderBottom: "2px dotted orangered", padding: "10px 0px 4px 10px", fontFamily: "system-ui"  }

    const styles = {color:"brown" , fontFamily:"ui-monospace" , fontWeight:"700" , fontSize:"large",textShadow:"1px 1px goldenrod"}
    return(
        <div>
            <h3 style={dateStyles}>{props.date}</h3>

            <div className='student-records' style={styles}>
            <div>SerNo</div>
            <div className='bdr-lft'>Name</div>
            <div className='bdr-lft'>RollNo</div>
            <div className='bdr-lft'>Course</div>
            <div className='bdr-lft'>Semester / Class</div>
            <div className='bdr-lft'>Branch / Section</div>
            <div>Attandance</div>
            </div>
        </div>

    ) 
}

const StudentsData = (props) => {
    return (
        <div className='student-records'>
            <div>{props.student.serNo}</div>
            <div className='bdr-lft'>{props.student.stdName}</div>
            <div className='bdr-lft'>{props.student.stdRollNo}</div>
            <div className='bdr-lft'>{props.student.stdCourse}</div>
            <div className='bdr-lft'>{props.student.stdClass}</div>
            <div className='bdr-lft'>{props.student.stdBranch}</div>
            <div className='bdr-lft' style={{backgroundColor:props.student.isPresent===''&&'yellow'}}>{props.student.isPresent}</div>

        </div>
    )

}


const StudentsRecord = (props) => {

    let record;
    localStorage.getItem(props.teaEmail + "Record") === null ? record = []
        : record = JSON.parse(localStorage.getItem(props.teaEmail + "Record"));

    let recordComponents = [];
    record.map((days, topIndex) => {
        if (typeof (days) === "object") {
            days.map((student , index) => {recordComponents.push(<StudentsData student={student} key = {topIndex*(index+topIndex)}/>)
            // console.log(topIndex*index+topIndex*topIndex)
        
        })
        }
        else {
            recordComponents.push(<Title date={days} key = {topIndex+''+props.teaEmail}/>)
            // console.log(topIndex*topIndex);
        }
        return days;
    })
    // console.log(recordComponents)
    // THE FOLLOWING OBJECT IS FOR THE H2 TAF WITH THE TEXT Students Record IN RETURN'S ORGUMENT;

    const styles = { textAlign: "center", color: "green", fontFamily: "systen-ui", border: "double", margin: "8px 0px" }
    return (
        <div>

            <h2 style={styles}>Students Record</h2>

            {recordComponents}
        </div>

    )
}

export default StudentsRecord;
