import React, { Component } from "react"
import MainSection from "./MainSection"
class App extends Component {
  constructor() {
    super()

    this.state = {
      teaName: "",
      teaEmail: '',
      teaPassword: "",

      islogin: false,
      isPresent : "",

      teaRegName: "",
      teaRegEmail: "",
      teaRegPassword: '',

      stdName: "",
      stdID: "",
      stdRollNo: "",
      stdCourse: "",
      stdClass: "",
      stdBranch: "",

      date:new Date().toLocaleDateString(),

      searchSno:2,
    }

  }

  changeHandler = (event) => {
    // console.log("changeHandler called")
    const { name, value } = event.target;

    this.setState({
      [name]: value
    })

  }

  teacherLogin = () => {
    // THIS IF ELSE IS USED TO CREATE TABLE FOR NEW STUDENTS, MEANS TO VISIBLE BOTH PRISENT AND ABSENT BUTTONS FOR ALL THE STUDENTS; 
    if(localStorage.getItem('previousDate') === null)
    {
      const date = new Date().toLocaleDateString();
      localStorage.setItem('previousDate' , JSON.stringify(date));
    }
    else
    {

      const date = new Date().toLocaleDateString(); 
      const previousDate = JSON.parse(localStorage.getItem('previousDate'))

      if(date !== previousDate){
        let students;
          localStorage.getItem(this.state.teaEmail)===null?students=[] :students = JSON.parse(localStorage.getItem(this.state.teaEmail))

        const newAttendance = students.map(student =>{
          student.isPresent = "" ;
          return student;
        })

        localStorage.setItem(this.state.teaEmail , JSON.stringify(newAttendance));
        localStorage.setItem('previousDate' , JSON.stringify(date));
        // console.log(this.state.teaEmail)
      }
    }


    if (this.state.teaName === "") {
      alert("PLEASE FILL THE NAME FEILD")
    }
    else if (this.state.teaEmail === "") {
      alert("PLEASE FILL THE ID FEILD")
    }
    else if (this.state.teaPassword === "") {
      alert("PLEASE FILL THE PASSWORD FEILD")
    }

    else {

      if (JSON.parse(localStorage.getItem("registeredTeachers")) === null) {
        alert("PEALSE REGISTER YOUR SELF")
      }
      else {
        // the following three variables used to check that the password email and name are matched or not
        let pass = false;
        let eml = false;
        let nam = false;
        const registeredTeachers = JSON.parse(localStorage.getItem("registeredTeachers"));
        registeredTeachers.map(person => {
          if (person.teaRegName === this.state.teaName) {
            nam = true;
            if (person.teaRegEmail === this.state.teaEmail) {
              eml = true;
              if (person.teaRegPassword === this.state.teaPassword) {
                pass = true;
                this.setState({ islogin: true, teaRegEmail: person.teaRegEmail })
              }
            }
          }
        })

        let msg = !nam ? "Name does not matched" : !eml ? "Email not matched" : !pass ? "password not matched" : null
        msg !== null && alert(msg);
      }
    }


  }

  toRegisteredAteacher = () => {
    // THE IF STATEMENT WILL CHECK THAT IS EVERY FIELD FULL OR NOT

    const msg = this.state.teaRegName === '' ? "Please enter your name ?" : this.state.teaRegEmail === '' ? "Please enter your Email ?" : this.state.teaRegPassword === '' ? "Please enter a password" : null;
    if (msg !== null)
      alert(msg);

    else {

      const teacher = {
        teaRegName: this.state.teaRegName,
        teaRegEmail: this.state.teaRegEmail,
        teaRegPassword: this.state.teaRegPassword
      }

      if (localStorage.getItem('registeredTeachers') === null) {
        let teachers = [];

        teachers.push(teacher);
        localStorage.setItem("registeredTeachers", JSON.stringify(teachers));
      }
      else {
        let teachers = JSON.parse(localStorage.getItem('registeredTeachers'));
        teachers.push(teacher);
        localStorage.setItem('registeredTeachers', JSON.stringify(teachers));
      }

      alert("You have Successfully registered")
      // console.log("teacher has been registered", teacher)
    }

  }

  addAstudent = () => {
    // The following two line code is used for form validation
    let msg = this.state.stdName === "" ? "Name" : this.state.stdRollNo === "" ? "Roll No" : this.state.stdCourse === "" ? "Course" : this.state.stdClass === "" ? "Class / Semester" : this.state.stdBranch === "" ? "Branch / Section" : null;
    msg !== null && alert("Please enter the student " + msg);

    if (msg === null) {

      let sno ;
      if(localStorage.getItem(this.state.teaRegEmail) === null)
      {
        sno = 1;
      }
      else
      {
        const array  = JSON.parse(localStorage.getItem(this.state.teaRegEmail));
        sno = array[array.length-1].serNo+1;
      }
        const stdObj = {
          serNo:sno,
          stdName: this.state.stdName,
          stdRollNo: this.state.stdRollNo,
          stdCourse: this.state.stdCourse,
          stdClass: this.state.stdClass,
          stdBranch: this.state.stdBranch,
          isPresent:this.state.isPresent,
          date :this.state.date
        }

      if (localStorage.getItem(this.state.teaRegEmail) === null) {
        const students = [];
        students.push(stdObj);
        localStorage.setItem(this.state.teaRegEmail, JSON.stringify(students));
        this.setState({serNo:students[students.length-1].serNo+1})
      }

      else {
        const students = JSON.parse(localStorage.getItem(this.state.teaRegEmail));
        

        students.push(stdObj);
        
        localStorage.setItem(this.state.teaRegEmail, JSON.stringify(students));
        this.setState({serNo:students[students.length-1].serNo+1});
        // console.log("Student added", msg, this.state.teaRegEmail)
      }
    }
    this.setState({ stdName: "", stdRollNo: ""})
  }

  present = (sno) =>{

    const std = JSON.parse(localStorage.getItem(this.state.teaRegEmail));
    const students = std.map(student =>{
      if(sno === student.serNo)
      {
        student.isPresent = "p"
      }
      return student;
    }) 
    
    localStorage.setItem(this.state.teaRegEmail , JSON.stringify(students));
    // console.log("present called with id ",sno)
    
    this.Record();
    this.setState({stdName:''});
  }

  absent = (sno) =>{

    const std = JSON.parse(localStorage.getItem(this.state.teaRegEmail));
    const students = std.map(student =>{
      if(sno === student.serNo)
      {
        student.isPresent = "a"
      }
      return student;
    }) 
    
    localStorage.setItem(this.state.teaRegEmail , JSON.stringify(students));
    this.Record();
    this.setState({stdName:''});
  }

delete = (sno)=>{

  const std = JSON.parse(localStorage.getItem(this.state.teaRegEmail));
  const students = std.filter((student)=>{
    return student.serNo !== sno
  })
  students.length === 0 ?localStorage.removeItem(this.state.teaRegEmail) : localStorage.setItem(this.state.teaRegEmail , JSON.stringify(students));
  
  this.setState({stdName:''})
}

Record = () =>{
  // console.log("Record function called");
  let date = new Date().toLocaleDateString();
  let lastAttendance = JSON.parse(localStorage.getItem(this.state.teaEmail));


  if(localStorage.getItem(this.state.teaEmail+"Record") === null)
  {
    const record =[];
    record.push(date,lastAttendance);
    localStorage.setItem(this.state.teaEmail+"Record",JSON.stringify(record)) 
  }

  else
  {
    const record = JSON.parse(localStorage.getItem(this.state.teaEmail+"Record"));
    lastAttendance.map(student => student.date = date);
    
    if(record[record.length-2] === date){
      record[record.length-1] = lastAttendance;
      localStorage.setItem(this.state.teaEmail+"Record" , JSON.stringify(record));
    }

    else{

      // THE FOLLOWING MAP FUNCTION IS USED TO CHANGED THE DATE OF THE PRESENT-DAY ATTENDANCE,TO GET A NEW DATE IN RECORD;
      lastAttendance.map(student =>student.date = date)

      record.push(date,lastAttendance);
      localStorage.setItem(this.state.teaEmail+"Record" , JSON.stringify(record));
    }
    
  }
}

searchIndividualRecord = ()=>
{
  let recordList=[]; // it will contain the data which matched to the given sno ;
  let records;  // it will retrive the data from the record component in the data base;  

  localStorage.getItem(this.state.teaEmail + "Record")===null? records=[]:records = JSON.parse(localStorage.getItem(this.state.teaEmail + 'Record'));

  records.map((record , topIndex)=>{
    typeof(record) === 'object' && record.map(student=>{student.serNo === parseInt (this.state.searchSno) && recordList.push(student) ;});
    return records;
  })

  // console.log('listComponent = ',recordList ,'Email' , this.state.teaEmail, 'sno = ' , this.state.searchSno );
}


  render() {
    return (
      <div>
        
        <MainSection
          changeHandler={this.changeHandler}
          teacherData={this.state}
          teacherLogin={this.teacherLogin}
          toRegisteredAteacher={this.toRegisteredAteacher}
          addAstudent={this.addAstudent}
          present = {this.present}
          absent = {this.absent}
          delete = {this.delete}
          searchIndividualRecord = {this.searchIndividualRecord}
        />
         
      </div>
    )
  }

}

export default App
