import React from 'react'

const DisplayClientsBody = (props) => {
    return(
    <div className='client-data-body-container'>
        <span
         className='delete' 
         title='Delete the client'
         onClick = {()=>props.deleteClient(props.data.id)}
        >X</span>
            <ul>
                <li>{props.data.id}</li>
                <li>{props.data.name}</li>
                <li>{props.data.mobile}</li>
                <li>{props.data.address}</li>

                <li  style={{overFlow:"none"}}>
                    <button
                        className='btn-indraft'
                       
                    >Indraft</button>
                </li>
            </ul>
    </div>
    )
}

const DisplayClient = (props) => {

    var allClients;

    localStorage.getItem('clients') === null? allClients =[]
    :allClients = JSON.parse(localStorage.getItem('clients'));
    
    const cleintsComponent = allClients.map(client =>{
       
       return <DisplayClientsBody
                 data = {client}
                 key = {client.id+''+client.mobile}
                 deleteClient = {props.deleteClient}
                />
       
    })

    return (
        <>
            <div className='display-client-container'>
                <div className='display-client-headings'>
                    <h3>ID</h3>
                    <h3>Name</h3>
                    <h3>Mobile #</h3>
                    <h3>address</h3>
                    <h3>Measurement</h3>
                </div>
                <img src="./booster.png" alt="" />
                <div><input type="text" /></div>
                {cleintsComponent}
            </div>
        </>
    )
}


export default DisplayClient;
