import React from 'react'
import MainSection from './MainSection'
import '../index.css'

class App extends React.Component{
    
    constructor(){
        super()
        this.state ={

            kameez :{
                kurta:true,
                sherwani:false,
                shirt:false,
            },

            kurtaShape:{
                rounded:false,
                squired:true,
            },

            shirtShape:{
                halfSleaves:false,
                fullSleaves:true
            },
            shalwarType:{
                paint:false,
                shalwar:true,
                trouser:false,
                patiala:true
            },

            kameezMeasurement:{
                height:40,
                shoulders:18,
                sleaves:23,
                chest:35,
                width:22,
            },
            
            shalwarMeasurement:{
                height:40,
                pancha:9
            },

            collorStyle:{
                EnglishCollor:true,
                bainCollor:false
            },

            clientData:{
                id:1,
                name:'Abbas',
                mobile:321,
                address:'Khan sher gari pabbi'
            },

        }
    }

    changeHandler = (e)=>{
        console.log('changeHandler called')
        const {name,value} = e.target;
        if(name === 'id'|| name==='name'||name==='mobile' || name === 'address'){
            
            this.setState(preState =>{
                return{
                    clientData:{
                        ...preState.clientData,
                        [name]:value
                    }
                }
            })

        }else{
            this.setState({ [name]:value})
        }
    }

    newClientRegistration = ()=>{
        console.log('customer registered')
    }

    render(){
        return(
            <div>
                <MainSection
                  state ={this.state} 
                  newClientRegistration = {this.newClientRegistration}
                  changeHandler = {this.changeHandler}
                />

            </div>
        )
    }
}

export default App;
