import React from 'react'

const SideBar = (props) => {
  return (
    <div className='sideBar-container'>
      <h3 style={{ textAlign: "center", color: "green" , marginTop:"20px" }}>New Student</h3>
      <div>
        <label htmlFor="std-name">Student Name
          <input type="text"
            id="std-name"
            name='stdName'
            value={props.studentData.stdName}
            onChange={props.changeHandler}
          />
        </label>

        <label htmlFor="std-RollNo">Roll No.
          <input type="text"
            id='RollNo'
            name='stdRollNo'
            value={props.studentData.stdRollNo}
            onChange={props.changeHandler}
          />
        </label>

        <label htmlFor="course">Course
          <input type="text"
            id='course'
            name='stdCourse'
            value={props.studentData.stdCourse}
            onChange={props.changeHandler}
          />
        </label>
        <label htmlFor="semester-class">Semester/Class
          <input type="text"
            id='semester-class'
            name='stdClass'
            value={props.studentData.stdClass}
            onChange={props.changeHandler}
          />
        </label>

        <label htmlFor="branch-section">Branch/Section
          <input type="text"
            id='branch-section'
            name='stdBranch'
            value={props.studentData.stdBranch}
            onChange={props.changeHandler}
          />
        </label>

        <button className="btn-sm-primery"
          style={{ backgroundColor: "green", borderColor: "darkgreen" }}
          onClick={props.addAstudent}
        >Admit</button>
      </div>
    </div>
  )
}

export default SideBar;
