import React from 'react'
import MainSection from './MainSection'
import '../index.css'

class App extends React.Component {

    constructor() {
        super()
        this.state = {

            kameez: {
                kurta: true,
                sherwani: false,
                shirt: false,
            },

            kurtaShape: {
                rounded: false,
                squired: true,
            },

            shirtShape: {
                halfSleaves: false,
                fullSleaves: true
            },
            shalwarType: {
                paint: false,
                shalwar: true,
                trouser: false,
                patiala: true
            },

            kameezMeasurement: {
                height: 40,
                shoulders: 18,
                sleaves: 23,
                chest: 35,
                width: 22,
            },

            shalwarMeasurement: {
                height: 40,
                pancha: 9
            },

            collorStyle: {
                EnglishCollor: true,
                bainCollor: false
            },

            clientData: {
                id: '',
                name: '',
                mobile: '',
                address: ''
            },

        }
    }

    changeHandler = (e) => {
        console.log('changeHandler called')
        const { name, value } = e.target;
        if (name === 'id' || name === 'name' || name === 'mobile' || name === 'address') {

            this.setState(preState => {
                return {
                    clientData: {
                        ...preState.clientData,
                        [name]: value
                    }
                }
            })

        } else {
            this.setState({ [name]: value })
        }
    }

    newClientRegistration = () => {
        const newClient = {
            id: this.state.clientData.id,
            name: this.state.clientData.name,
            mobile: this.state.clientData.mobile,
            address: this.state.clientData.address,
        }

        if (newClient.mobile.length > 11 || newClient.mobile.length < 11) {
            alert('Please Enter a valid mobile number')
        }

        else {
            if (localStorage.getItem('clients') === null) {
                const clients = [];
                clients.push(newClient);
                localStorage.setItem('clients', JSON.stringify(clients));
                alert('Registration successful')
            }

            else {
                var flage = false;
                const clients = JSON.parse(localStorage.getItem('clients'));

                clients.map(client => {
                    if (client.id === newClient.id) {
                        alert('This id is already exist')
                        console.log(client.id)
                        flage = true;
                    }
                    
                })
                if (!flage) {
                    clients.push(newClient);
                    localStorage.setItem('clients', JSON.stringify(clients));
                    alert('Registration successful')
                }
            }
        }


    }

    delete = (id) =>{
        console.log(id);
    }

    render() {
        return (
            <div>
                <MainSection
                    state={this.state}
                    newClientRegistration={this.newClientRegistration}
                    changeHandler={this.changeHandler}
                    delete = {this.delete}
                />

            </div>
        )
    }
}

export default App;
