import React from "react"
import TeacherLogin from './TeacherLogin'
import StudentsInfo from "./StudentsInfo"
import TeacherRegistrationForm from "./TeacherRegistrationForm"
import StudentsRecord from './StudentsRecord'
import IndividualRecord from "./IndividualRecord"
import About from './About'
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import Header from "./Header"

const MainSection = (props) => {

    return (
        <Router>
            <Header />
            <Routes>
                <Route exact path="/" element={
                    props.teacherData.islogin ? <StudentsInfo
                        addAstudent={props.addAstudent}
                        changeHandler={props.changeHandler}
                        studentData={props.teacherData}
                        present={props.present}
                        absent={props.absent}
                        delete={props.delete}
                    />
                        : <TeacherLogin
                            changeHandler={props.changeHandler}
                            teacherLogin={props.teacherLogin}
                            teacherData={props.teacherData}
                        />
                }></Route>

                <Route exact path="/teacher-registration-form" element={
                    <TeacherRegistrationForm
                        changeHandler={props.changeHandler}
                        registrationData={props.teacherData}
                        toRegisteredAteacher={props.toRegisteredAteacher}
                    />
                }></Route>

                <Route exact path="/students-record" element={
                    <StudentsRecord teaEmail={props.teacherData.teaEmail} />
                }></Route>

                <Route exact path="/individual-record" element={
                    <IndividualRecord
                        teaEmail={props.teacherData.teaEmail}
                        searchIndividualRecord={props.searchIndividualRecord}
                        changeHandler={props.changeHandler}
                        state={props.teacherData}
                    />
                }></Route>

                <Route exact path="/about" element={<About />}></Route>
            </Routes>
        </Router>
    )
}

export default MainSection;
