import React, { useState } from 'react'
import searchIcon from './search-icon.JPG'


const IndividualList = (props) => {
  return (
    <div className='individual-list' style={{ color: "aliceblue" }}>
      <div>{props.student.serNo}</div>
      <div>{props.student.stdRollNo}</div>
      <div>{props.student.date}</div>
      <div>{props.student.isPresent === '' ? ":(" : props.student.isPresent}</div>
    </div>
  )
}


const Individual_Record = (props) => {


  const [sno, setSno] = useState(0);
  const [display, setDisplay] = useState(false);

  let listComponent = []; // it will contain the data which matched to the given sno ;
  let records;  // it will retrive the data from the record component in the data base;  
  let studentName = '';
  let totalPreseance = 0;
  let totalAbsence = 0;


  localStorage.getItem(props.teaEmail + "Record") === null ? records = [] : records = JSON.parse(localStorage.getItem(props.teaEmail + 'Record'));

  records.map((record, topIndex) => {
    typeof (record) === 'object' && record.map((student, index) => {
      if (student.serNo === parseInt(sno)) {
        listComponent.push(<IndividualList student={student} key={topIndex + index * topIndex} />);
        student.isPresent === 'p' ? totalPreseance++ : totalAbsence++;
        studentName = student.stdName;
      }
    })
  })

  const containerStyles = { minHeight: '92.5vh', background: 'linear-gradient(120deg, #541f1f, rgb(41 137 135),#541f1f )' };

  const msgStyles = {color:"aliceblue" , fontFamily:"cursive",fontSize:"x-large", textAlign:"center"}

  return (

    <div style={containerStyles}>
      <div style={{ position: 'relative' }}>

        <input
          type="number"
          onChange={(e) => setSno(e.target.value)}
          className='search-bar'
          placeholder='Enter the student serNo'
          style={{ display: display ? "initial" : 'none' }}
        />
        <img
          src={searchIcon}
          title='Click to Search student data individually'
          className='search-icon'
          onClick={() => { setDisplay(!display) }}
          style={{ top: !display && "4px" }}
        />

      </div>
      {listComponent.length === 0 ? <div style={msgStyles}>Please enter a valid SerNo</div>
        : <div>
          <div className=' overView-container '>
          <div>Name  <span style={{ color: "#90e9e8" }}> {studentName} </span></div>
          <div>Preseance  <span style={{ color: "lightgreen" }}>{totalPreseance}/{listComponent.length}</span>Days</div>
          <div>Preseance %age  <span style={{ color: "lightgreen" }}>{Math.round((totalPreseance / listComponent.length) * 100)}</span></div>
          <div>Absence  <span style={{ color: "red" }}>{totalAbsence}/{listComponent.length}</span>Days</div>
          <div>Absence %age  <span style={{ color: "red" }}>{Math.round((totalAbsence / listComponent.length) * 100)}</span></div>
        </div>

          <div className='individual-list' style={{ fontWeight: "700", fontSize: "larger" }}>
            <div>SerNo</div>
            <div>Roll No</div>
            <div>Date</div>
            <div>Action</div>
          </div>
        </div>}
      {listComponent}

    </div>
  )
}

export default Individual_Record
